// ... existing code ...

import androidx.navigation.NavDestination

/**
 * NavDestination에서 Route 클래스명만 추출하는 확장 함수
 * 예: "com.speech.navigation.PracticeGraph.FeedbackRoute/..." -> "FeedbackRoute"
 */
fun NavDestination.getRouteClassName(): String {
 return route?.let { routeString ->
 // 파라미터 부분 제거 (? 이후)
 val routeWithoutParams = routeString.split("?").firstOrNull() ?: routeString
 // / 이전 부분만 가져와서 클래스명 추출
 val baseRoute = routeWithoutParams.split("/").firstOrNull() ?: routeWithoutParams
 // 마지막 점 이후의 클래스명만 추출
 baseRoute.split(".").lastOrNull() ?: "Unknown"
 } ?: "Unknown"
}
```

