```kotlin:baselineprofile/src/main/java/com/speech/baselineprofile/BaselineProfileGenerator.kt
package com.speech.baselineprofile

import androidx.benchmark.macro.junit4.BaselineProfileRule
import androidx.test.ext.junit.runners.AndroidJUnit4
import androidx.test.filters.LargeTest
import androidx.test.platform.app.InstrumentationRegistry
import androidx.test.uiautomator.By
import androidx.test.uiautomator.Until
import org.junit.Rule
import org.junit.Test
import org.junit.runner.RunWith

@RunWith(AndroidJUnit4::class)
@LargeTest
class BaselineProfileGenerator {

 @get:Rule
 val rule = BaselineProfileRule()

 @Test
 fun generate() {
 rule.collect(
 packageName = InstrumentationRegistry.getArguments().getString("targetAppId")
 ?: throw Exception("targetAppId not passed as instrumentation runner arg"),
 includeInStartupProfile = true
 ) {
 // Press home to ensure clean state
 pressHome()

 // Start the app and wait for it to be fully loaded
 startActivityAndWait()

 // Wait for the content to load completely
 device.wait(Until.hasObject(By.pkg("com.speech.speechmate")), 10_000)

 // Additional wait for app to stabilize
 Thread.sleep(3000)

 // Basic user interactions for common flows
 // Add any specific UI interactions here based on your app's main flows
 }
 }
}
```

