## 해결 방법

`::class` 참조 대신 다른 방식으로 해결해보겠습니다:

```kotlin:core/navigation/src/main/java/com/speech/navigation/NavUtil.kt
package com.speech.navigation

import android.util.Log
import androidx.navigation.NavDestination
import androidx.navigation.NavDestination.Companion.hasRoute
import androidx.navigation.NavDestination.Companion.hierarchy
import kotlin.reflect.KClass

// KClass 대신 qualifiedName을 직접 사용
private val HIDDEN_BOTTOM_BAR_ROUTES = setOf(
 "com.speech.navigation.SplashRoute",
 "com.speech.navigation.AuthGraph.LoginRoute",
 "com.speech.navigation.AuthGraph.OnBoardingRoute",
 "com.speech.navigation.PracticeGraph.RecordAudioRoute",
 "com.speech.navigation.PracticeGraph.RecordVideoRoute",
 "com.speech.navigation.PracticeGraph.FeedbackRoute",
 "com.speech.navigation.MyPageGraph.WebViewRoute",
)

fun NavDestination?.shouldHideBottomBar(): Boolean {
 val route = this?.route
 val shouldHide = route?.let { currentRoute ->
 HIDDEN_BOTTOM_BAR_ROUTES.any { hiddenRoute ->
 val matches = currentRoute.startsWith(hiddenRoute)

 // 디버깅용 로그
 Log.d("NavUtil", "Checking route: $currentRoute")
 Log.d("NavUtil", "Against hidden route: $hiddenRoute")
 Log.d("NavUtil", "Matches: $matches")

 matches
 }
 } ?: false

 // 최종 결과 로그
 Log.d("NavUtil", "=== shouldHideBottomBar Result ===")
 Log.d("NavUtil", "Current route: $route")
 Log.d("NavUtil", "Should hide: $shouldHide")
 Log.d("NavUtil", "Hidden routes: $HIDDEN_BOTTOM_BAR_ROUTES")
 Log.d("NavUtil", "================================")

 return shouldHide
}

fun NavDestination?.isRouteInHierarchy(route: KClass<*>): Boolean =
 this?.hierarchy?.any { it.hasRoute(route) } == true

fun NavDestination?.containsRoute(routes: List<KClass<*>>): Boolean {
 val currentRoute = this?.route ?: return false
 return routes.mapNotNull { it.simpleName }.any { currentRoute.contains(it) }
}

fun NavDestination.getRouteName(): String? = this.route?.let { mapRouteToName(it) }

private fun mapRouteToName(route: String): String? = when {
 route.startsWith("com.speech.navigation.AuthGraph.LoginRoute") -> "login"
 route.startsWith("com.speech.navigation.AuthGraph.OnBoardingRoute") -> "onboarding"
 route.startsWith("com.speech.navigation.PracticeGraph.PracticeRoute") -> "practice"
 route.startsWith("com.speech.navigation.PracticeGraph.RecordAudioRoute") -> "record_audio"
 route.startsWith("com.speech.navigation.PracticeGraph.RecordVideoRoute") -> "record_video"
 route.startsWith("com.speech.navigation.PracticeGraph.FeedbackRoute") -> "feedback"
 route.startsWith("com.speech.navigation.MyPageGraph.MyPageRoute") -> "my_page"
 route.startsWith("com.speech.navigation.MyPageGraph.SettingRoute") -> "setting"
 route.startsWith("com.speech.navigation.SplashRoute") -> "splash"
 else -> null
}
