package com.speech.app

import android.app.Application
import android.content.pm.PackageManager
import android.content.pm.Signature
import android.util.Base64
import android.util.Log
import com.speech.speechmate.BuildConfig
import com.kakao.sdk.common.KakaoSdk
import dagger.hilt.android.HiltAndroidApp
import java.security.MessageDigest
import java.security.NoSuchAlgorithmException

@HiltAndroidApp
class SpeechMateApplication : Application() {
 override fun onCreate() {
 super.onCreate()

 KakaoSdk.init(this, BuildConfig.KAKAO_NATIVE_APP_KEY)

 // 릴리즈 빌드에서도 키 해시를 출력 (카카오 개발자 콘솔 등록용)
 printKeyHash()
 }

 private fun printKeyHash() {
 try {
 val packageInfo = packageManager.getPackageInfo(packageName, PackageManager.GET_SIGNATURES)
 for (signature: Signature in packageInfo.signatures) {
 val md: MessageDigest = MessageDigest.getInstance("SHA")
 md.update(signature.toByteArray())
 val keyHash = Base64.encodeToString(md.digest(), Base64.NO_WRAP)
 Log.d("KAKAO_KEY_HASH", "Key Hash: $keyHash")

 // 릴리즈 빌드에서도 확인 가능하도록 System.out에도 출력
 println("=== KAKAO KEY HASH ===")
 println("Key Hash: $keyHash")
 println("=====================")
 }
 } catch (e: PackageManager.NameNotFoundException) {
 Log.e("KAKAO_KEY_HASH", "Package not found", e)
 } catch (e: NoSuchAlgorithmException) {
 Log.e("KAKAO_KEY_HASH", "No such algorithm", e)
 }
 }
}
