 val builder = OkHttpClient.Builder()
 .connectTimeout(15, TimeUnit.SECONDS)
 .readTimeout(180, TimeUnit.SECONDS)
 .writeTimeout(180, TimeUnit.SECONDS)

 return builder.build()
 }

