 onRequestFeedback: () -> Unit,
 onStartRecording: () -> Unit,
 onFinishRecording: () -> Unit,
 onCancelRecording: () -> Unit,
 onPauseRecording: () -> Unit,
 onResumeRecording: () -> Unit,
 onSpeechConfigChange: (SpeechConfig) -> Unit,
