```kotlin:core/designsystem/src/main/java/com/speech/designsystem/component/Circle.kt, lines=ALL(1-50)
package com.speech.designsystem.component


import androidx.compose.foundation.Canvas
import androidx.compose.foundation.layout.size
import androidx.compose.runtime.Composable
import androidx.compose.ui.Modifier
import androidx.compose.ui.geometry.Offset
import androidx.compose.ui.graphics.Color
import androidx.compose.ui.graphics.drawscope.Stroke
import androidx.compose.ui.unit.Dp
import androidx.compose.ui.unit.dp
import com.speech.designsystem.theme.SmTheme

@Composable
fun SimpleCircle(
 modifier: Modifier = Modifier,
 color: Color = SmTheme.colors.primaryDefault,
 diameter: Dp = 90.dp
) {
 Canvas(modifier = modifier.size(diameter)) {
 val radius = size.minDimension / 2f
 drawCircle(
 color = color,
 radius = radius,
 center = Offset(x = size.width / 2f, y = size.height / 2f)
 )
 }
}

@Composable
fun StrokeCircle(
 modifier: Modifier = Modifier,
 color: Color = SmTheme.colors.border,
 diameter: Dp = 50.dp,
 strokeWidth: Dp = 2.dp
) {
 Canvas(modifier = modifier.size(diameter)) {
 val strokePx = strokeWidth.toPx()

 val radius = size.minDimension / 2f - strokePx / 2f

 drawCircle(
 color = color,
 radius = radius,
 style = Stroke(width = strokePx)
 )
 }
}

```
