 fileUrl = fileUrl,
 speechFileType = speechFileType,
 speechConfig = state.speechConfig,
 ),
 )
 }.onFailure {
 if (it !is CancellationException) {
 postSideEffect(PracticeSideEffect.ShowSnackBar("발표 파일 업로드에 실패했습니다."))
 }
 }.also {
 reduce {
 state.copy(speechConfig = SpeechConfig(), uploadFileStatus = null)
 }
 }
 }
 }

 private fun onCancelUploading() {
 uploadJob?.cancel()
 uploadJob = null
 intent {
 reduce {
 state.copy(uploadFileStatus = null)
 }
 postSideEffect(PracticeSideEffect.ShowSnackBar("업로드를 취소했습니다."))
 }
 }

 private fun onProgressUpdate(status: UploadFileStatus) = intent {
 reduce {
 state.copy(uploadFileStatus = status)
 }
 }
}
```

